    if x>display_width- car_width or x<0:
        gameExit=True
        crushed()

    if thing_starty>display_height:
        thing_starty= 0-thing_height
        thing_startx = random.randrange(0,display_width)

    # проверяем на обновления дисплея
    pygame.display.update()
    # кадры в секунду = 60
    clock.tick(60)



